export function getCursorRuleByKey(key: string): CursorRule | undefined {
  return AVAILABLE_CURSOR_RULES.find(rule => rule.key === key);
}

export function searchCursorRules(searchTerm: string): CursorRule[] {
  const term = searchTerm.toLowerCase();
  return AVAILABLE_CURSOR_RULES.filter(rule => 
    rule.key.toLowerCase().includes(term) || 
    rule.name.toLowerCase().includes(term)
  );
}

export const CURSOR_RULE_KEYS = AVAILABLE_CURSOR_RULES.map(rule => rule.key);
